deployment.content:
@startuml Deployment Diagram
!theme plain

title Deployment Architecture - hello-world

node "Client" as client {
  artifact "Web Browser" as browser
}

node "Application Server" as appServer {
  component "API Gateway" as apiGateway
  component "Business Logic" as logic
}

node "Data Layer" as dataLayer {
  database "Primary Database" as db
  artifact "Cache Storage" as cache
}

client --> appServer
appServer --> dataLayer
apiGateway --> logic
logic --> db
logic --> cache

@enduml

